INSERT INTO `ecs_goods` VALUES (19, 3, 'ECS000019', '三星SGH-F258', '+', 7, 6, '', 12, 0.000, 1029.60, 858.00, 0.00, 0, 0, 1, '滑蓋 GSM 258 時尚 藍牙 黑色 金色 白色', '從整體來看，三星SGH-F258比較時尚可愛，三圍尺寸為94×46×17.5mm，重量為96克，曲線柔和具有玲瓏美感\r\n', '<p>從整體來看，三星SGH-F258比較<font size="3" color="#ff0000"><strong>時尚</strong></font>可愛，三圍尺寸為94&times;46&times;17.5mm，重量為96克，曲線柔和具有玲瓏美感。</p>\r\n<p>&nbsp;</p>\r\n<p>三星F258的前面板整體應用<strong><font size="3" color="#ff0000">鏡面效果</font></strong>，2.0英寸顯示屏幕略顯窄小，導航鍵與功能鍵在滑蓋滑開之前是前面板的全部控制鍵，沒有掛斷和接聽鍵，不過，五向鍵有<font size="3"><strong><font color="#ff0000">音樂操</font><font color="#ff0000">作</font></strong></font>功能，明確顯示了F258音樂手機的定位。</p>\r\n<p>&nbsp;</p>\r\n<p>F258的數字鍵鍵區采用了極具金屬質感的平板式設計，每顆按鍵的面積足夠大，按鍵與按鍵之間的間隔明顯，操作起來手感舒適。</p>', 'images/200905/thumb_img/19_thumb_G_1241970175208.jpg', 'images/200905/goods_img/19_G_1241970175091.jpg', 'images/200905/source_img/19_G_1241970175549.jpg', 1, '', 1, 1, 0, 8, 1241970139, 100, 0, 1, 1, 1, 0, 0, 1269589154, 9, '', -1, -1, NULL, NULL);
INSERT INTO `ecs_goods` VALUES (20, 3, 'ECS000020', '三星BC01', '+', 14, 6, '', 12, 0.000, 336.00, 280.00, 238.00, 1241884800, 1251648000, 1, 'GSM 直板 40和弦 金屬材質 2008年07月 黑色 GSM,900,1800,1900,2100', '', '', 'images/200905/thumb_img/20_thumb_G_1242106490058.jpg', 'images/200905/goods_img/20_G_1242106490663.jpg', 'images/200905/source_img/20_G_1242106490922.jpg', 1, '', 1, 1, 0, 2, 1241970417, 100, 0, 1, 1, 1, 1, 0, 1269589110, 9, '', -1, -1, NULL, NULL);
INSERT INTO `ecs_goods` VALUES (21, 3, 'ECS000021', '金立 A30', '+', 4, 10, '', 40, 0.000, 2400.00, 2000.00, 0.00, 0, 0, 1, '2008年03月 GSM,900,1800,1900,2100 直板 1600萬 240×400 像素 2007年12月 200萬攝像頭 黑色', '', '<p>&nbsp;</p>\r\n<p align="left"><span style="font-size: medium;"><font size="3">標準配置: 標準鋰電池兩塊,充電器兩個,立體聲耳機一個,數據線,說明書送256TF</font></span></p>\r\n<p align="left"><span style="font-size: medium;"><font size="3">通訊功能&nbsp; 輸入法 短信功能 話機通訊錄 通話記錄</font></span></p>\r\n<p align="left"><span style="font-size: medium;"><font size="3">輸入法: 中文輸入法</font></span></p>\r\n<p align="left"><span style="font-size: medium;"><font size="3">短信功能: 支持短信300條和彩信100條</font></span></p>\r\n<p align="left"><span style="font-size: medium;"><font size="3">話機通訊錄: 1000組名片式電話存儲 來電防火墻功能&nbsp; 來電大頭貼及來電鈴聲識別</font></span></p>\r\n<p align="left"><span style="font-size: medium;"><font size="3">通話記錄：支持電話錄音,恢復/靜音,雙音多頻,免提</font></span></p>\r\n<p>&nbsp;</p>\r\n<p><font size="3">高級功能</font></p>\r\n<p align="left"><span style="font-size: medium;"><font size="3">多媒體娛樂: 200萬像素，最大支持10240*960，11級數碼變焦,多種效果設置,連拍可全屏查看拍攝或下載的圖片.支持有聲MP4短片拍攝，錄像時間根據內存 大小而定，拍攝錄像,TV電視可跟朋友家人一起分享。支持網絡攝像頭功能 支持MPEG4、3GP(H.263)等視頻格式 支持MP3等音頻格式播放， 支持歌詞播放</font></span></p>\r\n<p align="left"><span style="font-size: medium;"><font size="3">內置遊戲:智能拼圖</font></span></p>\r\n<p align="left"><span style="font-size: medium;"><font size="3">擴展存儲功能:支持TF擴展,支持U盤功能,本機內置1M,隨機贈送256TF卡</font></span></p>\r\n<p align="left"><span style="font-size: medium;"><font size="3">高級功能:直板/智能PDA手寫/鍵盤輸入 超長待機 WAP上網&nbsp; 錄音 藍牙 FM收音機 電子書 Java擴展 TV電視輸出 聲控語音撥號 免費電視 來電防火墻&nbsp;&nbsp;&nbsp;</font></span></p>\r\n<p align="left"><span style="font-size: medium;"><font size="3">附加功能: 日歷 計算器 鬧鐘 記事本 日程表 秒表 單位換算</font></span></p>\r\n<p><span style="font-size: medium;"><font size="3">提醒 以上性能均為本公司員工試機後所寫,產品資料僅供參考,請以收到實物為準</font></span></p>', 'images/200905/thumb_img/21_thumb_G_1242109298150.jpg', 'images/200905/goods_img/21_G_1242109298873.jpg', 'images/200905/source_img/21_G_1242109298831.jpg', 1,